import React from 'react';
import { BiTennisBall } from 'react-icons/bi';
import './Button.css';

const STYLES=['btn--primary','btn--outline'];
const SIZES=['btn--medium','btn--large','btn--mobile','btn--wide'];
const COLOR=['primary','blue','red','green'];

export const Button=({
    children,
    type,
    onClick,
    buttonStyle,
    buttonSize,
    buttonColor
})=>{
    const checkButtonStyle=STYLES.include(buttonStyle)?buttonStyle:STYLES[0];
    const checkButtonSize=SIZES.include(buttonSize)?buttonSize:SIZES[0];
    const checkButtonColor=COLOR.include(buttonColor)?buttonColor:COLOR[0];

    return (
        <button className={`btn ${checkButtonStyle} ${checkButtonColor} ${checkButtonSize}`} onClick={onClick} type={type}>
            {children}
            </button>
    )

}
