import styled from 'styled-components'

export const Styles =styled.div`

table{
    border-spacing:0;
    border:1px solid black;

    th,td{
        padding:25px;
    
    }
    
    th{
        background:#a7aaab;
        color:white;
        border-bottom:3px solid #747778;
        
    }

    tr {     
        &:nth-child(even) { background-color: white; }
        &:nth-child(odd) { background-color: lighten(#398B93, 55%) }
     }

}


`
